REPLACE INTO rvs_project_info (project_info_id,template_item_id,font_id,navigator_id,rvs_category_id,charset_id,project_company,project_slogan,project_logo,project_title,project_keyword,project_description,project_domain,check_step,effect_site_enter,duration_site_enter,last_update,layout_template_id,i_pathway,i_mail,i_print,i_favorite,no_slogan,no_company_name,navigator_name_id) VALUES ('e6e9a6615d09edda8f494bcbc44599c8','f29f5a966e502d1f87281d7467442267','12','c91c36fd22587c22b00d5a3575661e76','17','17','<h1>Resolute</h1>','','NOPIC','Resolute Academy - NO TAKS to graduate','','','resoluteacademy.com','4',null,null,'1252887728','10707e258bbbed8d5c3fb9779f829a39','1','0','1','0','1','1','9');
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('77bcabd0a351c3bb09c8513e7699df91','192bad13ce52829df6c18ec7784647b2','Home','','0','<blockquote>\r\n<blockquote>\r\n<h1 align=\"center\"><strong><span class=\"hddpg\"><font color=\"#ff0000\">No Exit Exam Required for Graduation! </font></span></strong></h1>\r\n<h1 align=\"center\"><strong><span class=\"hddpg\"><br />\r\n</span></strong></h1>\r\n<p>Eduardo had completed all of his credits.&nbsp; He should have been a\r\nhigh school graduate, seeing his dreams achieved.&nbsp; What happened?&nbsp;\r\nEduardo was like many students in that he had difficulty passing the\r\nstate testing required in order to receive his high school diploma.</p>\r\n<p>Resolute Academy was born to help students just like Eduardo.</p>\r\n<p>Coby Cathey, an award winning educator and author, recognized the\r\ndevastating effects of a system that failed to take into account test\r\nanxiety, unique life circumstances and the demoralizing toll caused by\r\nthe Texas Assessment of Knowledge and Skills.</p>\r\n<p>He created Resolute Academy to help students negatively impacted by state testing.&nbsp;</p>\r\n<p>Once students have completed all of their credits, they can be\r\ntransferred to Resolute Academy and receive the proper recognition for\r\nthe efforts of their educational career.</p>\r\n<p>A diploma is well within your reach.&nbsp; Be proud.&nbsp; Stand tall.&nbsp; Make more money and achieve the success waiting for you, now!</p>\r\n<h1 align=\"left\"><span class=\"hddpg\"><font size=\"2\">What if you could already be a high school graduate from Resolute Academy?</font></span></h1>\r\n<div align=\"left\">\r\n<ul>\r\n<li><span class=\"hddpg\"><font size=\"2\">No state exam required to receive a diploma</font></span></li>\r\n<li><span class=\"hddpg\"><font size=\"2\">Use the credits you have already earned</font></span></li>\r\n<li><span class=\"hddpg\"><font size=\"2\">You may have enough credits already to receive your diploma</font></span></li>\r\n<li><span class=\"hddpg\"><font size=\"2\">You never enter a classroom</font></span></li>\r\n<li><span class=\"hddpg\"><font size=\"2\">Online courses</font></span></li>\r\n<li><span class=\"hddpg\"><font size=\"2\">Dual college credit courses available</font></span></li></ul><font size=\"2\"><strong>You can continue doing what you\'re doing or you could be...</strong></font></div>\r\n<div align=\"left\">\r\n<ul>\r\n<li>Attending community college</li>\r\n<li>Attending trade school</li>\r\n<li>Protecting our country in the military</li>\r\n<li>Attending university</li>\r\n<li>Making $2 more per hour than you are now</li></ul>\r\n<div align=\"center\"><font size=\"3\" color=\"#ff0000\"><strong>It\'s up to you.</strong></font></div></div>\r\n<div align=\"left\">&nbsp;</div>\r\n<div align=\"left\">Click on the <a href=\"affcaf5a8b162a7494b04f1f189ea2b2.php\" title=\"\">Contact Us</a> above and let us help you take the next step!</div></blockquote></blockquote><br />','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('38f8d886085f14f206fa4c50a163e5ef','192bad13ce52829df6c18ec7784647b2','About us','','0','<blockquote>\r\n<blockquote><font face=\"Verdana\"><img style=\"margin: 10px\" alt=\"\" title=\"\" src=\"/3rdparty/rvsitebuilder/project/9a2a5ad1d604bbde7f1ac39c6f522f3b/images/cobyresolute.jpg\" align=\"left\" height=\"139\" width=\"130\" />Coby Cathey, president of Resolute Academy graduated from Harding\r\nUniversity and East Texas State University.&nbsp; <br />\r\n<br />Coby\r\nhas served as a\r\nteacher at the middle school and high school levels.&nbsp; While teaching\r\nAstronomy at Newman Smith High School in Carrollton, TX, he created the\r\nNewman Smith Space Project.&nbsp; The project was an interdisciplinary\r\nall-school activity, involving over 2500 students in a mission to\r\ncreate a space simulator that would house 4 student astronauts, 72\r\nhours.&nbsp; The project received regional and national attention as an\r\ninnovative and landmark experience for students and educators alike.&nbsp;\r\nAs a result, Coby received local and national recognition including\r\nawards from NASA and the National Science Teachers Association.<br />\r\n<br />Coby served as an assistant principal and principal in several\r\nat-risk schools.&nbsp; He repeatedly saw students that were negatively\r\nimpacted by the state testing as he describes in his own words.<br />\r\n<br /></font>\r\n<blockquote><font face=\"Verdana\">&quot;I\r\nwould see students that had completed all of the required coursework,\r\nyet could not pass the state testing.&nbsp; There were a variety of reason,\r\nbut the unfortunate thing was these students were being held back,\r\nunable to achieve their dreams, left with a bad taste about the\r\neducational system.</font><br />\r\n</blockquote>\r\n<blockquote><font face=\"Verdana\">Initially, I thought they should just try harder.&nbsp; I developed\r\nseveral instructional strategies to assist students in the process.&nbsp; It\r\nwasn\'t until I really analyzed the available tests and realized it was\r\na system that was inherently broken and designed to see a certain\r\nnumber of students fail.&nbsp; Let me say that again, the system in place,\r\nis designed to insure that a certain number of students will NEVER be\r\nsuccessful.&nbsp; Where is the research to indicate state testing improves\r\nthe quality of student, their work ethic, the quality of education or\r\nhas any benefit?&nbsp; The only benefit of state testing is to the test\r\nmakers and the lobbiests and politicians that favor such.&nbsp; The current\r\npractice is a travesty and a farce as millions of dollars are being\r\nspent annually.&nbsp; <br />\r\n</font></blockquote>\r\n<blockquote><font face=\"Verdana\">\r\nThe weakest members of society, those with the smallest voice,\r\nat-risk and disadvantaged students, are the ones that are suffering the\r\nmost in the current system.&nbsp; </font><br />\r\n<font face=\"Verdana\"></font><br />\r\n<font face=\"Verdana\">I created Resolute Academy to see\r\nthat students receive the recognition for their 12 years of work and\r\ndedication.&nbsp; It is my prayer that every student that has a negative\r\nfeeling about education finds a way to see their full potential and\r\nfind a path that works for them.&nbsp; I believe Resolute Academy can be\r\nthat path.&quot;</font><br />\r\n</blockquote><font face=\"Verdana\"><br />\r\nCoby has also conducted\r\ntraining sessions for educators and administrators on a wide range of\r\ntopics including classroom management, safety, student motivation,\r\nparenting and curriculum design.</font></blockquote></blockquote>','','','','',null,null,'','','','',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('affcaf5a8b162a7494b04f1f189ea2b2','192bad13ce52829df6c18ec7784647b2','Contact Us','','163204c9dcabc700cafa985b466acba3','<img alt=\"\" id=\"onlineform_ca9a62ba400d19072dae07681b6a674f\" name=\"onlineform_ca9a62ba400d19072dae07681b6a674f\" src=\"/3rdparty/rvsitebuilder/themes/default/sitebuilder/images/layout_onlineform.jpg\" border=\"1\" height=\"110\" width=\"330\" /><br />\r\n<br /><br />\r\n<br />\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>','','','','',null,null,'','','','5bfe7e4bc4d56dd4d5d9a9dafd6a2045',null,null,null,null);
REPLACE INTO rvs_project_page (project_page_id,rvs_usr_id,page_name,custom_page_name,rvs_component_id,page_data,head,page_title,meta_keyword,meta_description,effect_page_enter,duration_page_enter,meta_page_effect,rvs_link_id,target,form_template_id,i_pathway,i_mail,i_print,i_favorite) VALUES ('36c14c38d67129b79d6e40d40c99a618','192bad13ce52829df6c18ec7784647b2','FAQ','','0','<blockquote>\r\n<div><font color=\"#1010ef\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\"><span style=\"font-size: 8pt\" lang=\"en-US\"></span></span></font></div>\r\n<div><font color=\"#1010ef\" size=\"2\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\"><span style=\"font-size: 8pt\" lang=\"en-US\"></span></span></font></div><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">What does Resolute do?</span><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">\r\n<p style=\"text-align: left\" class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">Resolute is a private school. We award high school credit to students whether\r\nthey are homeschooled or received their credits in a public school\r\nclassroom.&nbsp; We are a state-recognized, nationally accredited private school\r\noffering to evaluate the coursework of high school students, and upon\r\nadequate documentation and evidence (your transcript), we grant high\r\nschool credits.</span></p></span>\r\n<div><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">\r\nResolute is a private school that allows students to transfer their work\r\ninto our school. And, when a student has accumulated all of the\r\nrequired credits, he or she is eligible to receive a high school\r\ndiploma.</span></div><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\"></span>\r\n<div style=\"text-align: left\" class=\"MsoNormal\">&nbsp;</div>\r\n<div style=\"text-align: left\" class=\"MsoNormal\"><font color=\"#0000ff\">How many credits are required for graduation?</font></div>\r\n<div style=\"text-align: left\" class=\"MsoNormal\">&nbsp;</div>\r\n<div style=\"text-align: left\" class=\"MsoNormal\">Resolute Academy has multiple degree plans. The basic diploma can be received with 19 credits. </div><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">\r\n<p style=\"text-align: left\" class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">Is This a REAL diploma? </span></p></span>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">We\r\nhave the authority and privilege as a private school, to grant high\r\nschool diplomas. And with that authority comes the responsibility for\r\ngranting high school credits. Before Resolute awards credits, we must verify successful completion of 19\r\ncredit hours . This is validated\r\nby using an official transcript from the originating high school where\r\nthe student attended. Every\r\nfile is confidential, according to the FERPA requirements, and we\r\nrelease student information only after a release form is signed by the\r\nparent, legal guardian, or adult student.<br />\r\n<br />\r\nOnce the documentation is there, the credit is granted. Once the\r\ncredits are granted, the diploma is awarded. Yes, this is a real high\r\nschool diploma -- NOT a GED, NOT a certificate of completion.</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: #548dd4; font-family: arial\" lang=\"en-US\"></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">What if I live out of state?</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">In today&#8217;s educational world, living in one state and graduating from a school in another state is no longer unusual.&nbsp; With distance learning, satellite classes, video courses, online\r\nclassrooms, military families, job transfers, government employee\r\nassignments, etc., it is not uncommon for students to have these\r\narrangements.&nbsp; No residency is required. No on-site attendance is required.\r\nAll Resolute graduates, regardless of their places of residence, are\r\nawarded a diploma from Resolute.</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">Will colleges accept a Resolute diploma?</span></p>\r\n<div class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">Yes, colleges DO accept the Resolute diploma. Admission to college is determined by the requirements of&nbsp; each college. Such requirements include a high school diploma, SAT/ACT scores, and other factors unique to the university. </span></div>\r\n<div class=\"MsoNormal\">&nbsp;</div>\r\n<div class=\"MsoNormal\">\r\n<p><font color=\"#0000ff\">Can I take classes from Resolute Academy?</font></p>\r\n<p>Resolute Adacemy is in the process of developing online classes.&nbsp; The online curriculum will be available in August.</p>\r\n<p><font color=\"#0000ff\">I still need a class(es) in order to have all of my credits.&nbsp; Can I still get a diploma?</font></p>\r\n<p>Send us your transcript.&nbsp; We will be happy to help guide you in the next step of achieving your dreams, now.</p></div>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">Is a Resolute diploma better than a GED? </span><span style=\"font-size: 9pt; color: #548dd4; font-family: arial\" lang=\"en-US\"></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">Here are a few things you should know about getting a GED. <br />\r\n<br /></span></p>\r\n<blockquote><span style=\"font-weight: bold; font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">A.</span><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">\r\nIn most states, generally, you must be 18 to take the GED without\r\nrestrictions. Sometimes 17-year-olds are allowed after filing a special\r\nform, and if they can show special need. Different states have\r\ndifferent rules. </span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><span style=\"font-weight: bold; font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">B.</span><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">\r\nHere&#8217;s a very important question -- how will you USE your GED? For\r\nexample, if you are applying for military enlistment, the GED is almost\r\nuseless. And there are other problems associated with it, such as\r\nscholarships and grants. </span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">\r\nMost PRIVATE scholarships, trusts, organizations, and scholarship\r\ncommittees automatically make graduation from a recognized secondary\r\ninstitution a prerequisite for granting their scholarship money. This\r\nis a SCHOLARSHIP, and having a GED is not enough. </span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span><br />\r\n<span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\"></span>\r\n<div class=\"MsoNormal\"><span style=\"font-weight: bold; font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">C.</span><span style=\"font-size: 9pt; color: black; font-family: arial\" lang=\"en-US\">\r\nIt is unfortunate, but true, that the GED has a stigma attached to it.\r\nHaving a GED sends a warning to the those concerned that you had\r\ntrouble in school -- you were thrown out, or you got pregnant, or you\r\nwere in a treatment center, or you were violent, or you were on drugs,\r\nor you couldn\'t get along with others, or you couldn\'t conform, and you\r\nwere generally different. Ultimately it means you had to settle for\r\nless than normal.</span></div></blockquote>\r\n<div class=\"MsoNormal\">&nbsp;</div>\r\n<div class=\"MsoNormal\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">How much does it cost?&nbsp; </span></div>\r\n<div class=\"MsoNormal\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\">&nbsp;</span></div>\r\n<div class=\"MsoNormal\"><font color=\"#000000\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\"></span></font>Unlike most private schools that charge thousands of dollars per year, Resolute Academy has a one-time charge of $250. Every graduate will receive an attractively bound diploma and 5 copies of your transcript. </div>\r\n<div class=\"MsoNormal\">&nbsp;</div>\r\n<div class=\"MsoNormal\"><span style=\"font-size: 9pt; color: blue; font-family: arial\" lang=\"en-US\"></span>&nbsp;</div>\r\n<div class=\"MsoNormal\">&nbsp;</div></blockquote>','','','','',null,null,'','','','',null,null,null,null);
